import csv 

input_csv_path = 'data_new_albedo_3eb.csv'
output_csv_path = 'data_new_albedo_3eb_n.csv'

with open(input_csv_path, mode='r') as input_csv_file:
    csv_reader = csv.reader(input_csv_file)
    header = next(csv_reader)  # Read and skip the header row
    rows = list(csv_reader)

# Sort the rows based on the values in the first column
sorted_rows = sorted(rows, key=lambda row: row[0])

# Insert the header row at the beginning of the sorted rows
sorted_rows.insert(0, header)

# Write the sorted rows to a new CSV file
with open(output_csv_path, mode='w', newline='') as output_csv_file:
    csv_writer = csv.writer(output_csv_file)
    csv_writer.writerows(sorted_rows)

print("CSV sorted and saved as", output_csv_path)